13     private static int partition(int[] arr, int left, int right) &amp;#123;
14         //ÈÄâÂèñ‰∏≠ËΩ¥ÂÖÉÁ¥†
15         int pivot = arr[left];
16         int i = left + 1;
17         int j = right;
18         while (true) &amp;#123;
19             // ÂêëÂè≥ÊâæÂà∞Á¨¨‰∏Ä‰∏™Â∞è‰∫éÁ≠â‰∫é pivot ÁöÑÂÖÉÁ¥†‰ΩçÁΩÆ
20             while (i &amp;lt;= j &amp;amp;&amp;amp; arr[i] &amp;lt;= pivot) i++;
21             // ÂêëÂ∑¶ÊâæÂà∞Á¨¨‰∏Ä‰∏™Â§ß‰∫éÁ≠â‰∫é pivot ÁöÑÂÖÉÁ¥†‰ΩçÁΩÆ
22             while(i &amp;lt;= j &amp;amp;&amp;amp; arr[j] &amp;gt;= pivot ) j--;
23             if(i &amp;gt;= j)
24                 break;
25             //‰∫§Êç¢‰∏§‰∏™ÂÖÉÁ¥†ÁöÑ‰ΩçÁΩÆÔºå‰ΩøÂæóÂ∑¶ËæπÁöÑÂÖÉÁ¥†‰∏çÂ§ß‰∫épivot,Âè≥ËæπÁöÑ‰∏çÂ∞è‰∫épivot
26             int temp = arr[i];
27             arr[i] = arr[j];
28             arr[j] = temp;
29         &amp;#125;
30         arr[left] = arr[j];
31         // ‰Ωø‰∏≠ËΩ¥ÂÖÉÁ¥†Â§Ñ‰∫éÊúâÂ∫èÁöÑ‰ΩçÁΩÆ
32         arr[j] = pivot;
33         return j;
34     &amp;#125;
35 &amp;#125;
